import "../App.css";
import KeyVending from "./KeyVending";
import ScreenVending from "./ScreenVending";
import React, { useState } from "react";

const Vending = ({ Check }) => {
  const [content, setcontent] = useState("");
  const HandleClick = (contentx) => {
    var NewKey = content + contentx;
    setcontent(NewKey);
    if (NewKey.length == 2) {
      const Id = Check.Check.forEach((bW) => {
        if (bW.Type == Check.Waterr) {
          return bW.id;
        }
      });
      if (Id == content) {
        console.log("Dung");
      } else {
        NewKey = "";
        setcontent(NewKey);
      }
    }
  };
  return (
    <div>
      <ScreenVending Content={content} />
      <KeyVending handleClick={HandleClick} />
    </div>
  );
};
export default Vending;
